#include<bits/stdc++.h>using namespace std;int n,k,ans,f[505],t,f1[505];char s[505];void dfs(int x,int y,int z){	if(x==n+1)	  {	  	if(y||s[n]=='*')	  	  return;	  	ans++;	  	return;	  }	if(s[x]=='(')	  f[++t]=z,f1[t]=x,dfs(x+1,y+1,z);	else	  if(s[x]=='*')	    {	    	if(z+1-f[t]>k||t>1&&f[t]-f[t-1]&&s[x-1]==')')	    	  return;	    	dfs(x+1,y,z+1);		}	  else	    if(s[x]==')')	      {	      	if(!y||s[x-1]==')'&&f1[t-1]+1==f1[t]) 	      	  return;	      	dfs(x+1,y-1,f[t-1]);		  }		else		  {			  	if(y)		  	  s[x]=')',dfs(x+1,y-1,f[t-1]);		  	if(z+1-f[t]<=k)		  	  s[x]='*',dfs(x+1,y,z+1);		  	s[x]='(';f[++t]=z;f1[t]=x;		  	dfs(x+1,y+1,z);t--;s[x]='?';		  }}int main(){	freopen("bracket.in","r",stdin);	freopen("bracket.out","w",stdout);	scanf("%d%d",&n,&k);	scanf("%s",s+1);	dfs(1,0,0);cout<<ans;    return 0;}/*7 3(*??*??10 2???(*??(?)**((*())*)*/