#include<bits/stdc++.h>using namespace std;int T,n,a[500005],f[500005];char f1[500005];int read(){	int x=0,f=1;	char ch=getchar();	while(!isdigit(ch))	{	  if(ch=='-')		f=-1;	  ch=getchar();	}	while(isdigit(ch))	{	  x=x*10+ch-'0';	  ch=getchar();	}	return x*f;}int dfs(int x,int y){	if(x==y)	  return 1;	int e=2*n-(y-x+1);	if(e<n)	  {	  	f[e+1]=a[x];f1[e+1]='L';	  	if(dfs(x+1,y))	  	  return 1;	  	f[e+1]=a[y];f1[e+1]='R';	  	return dfs(x,y-1);	  }	if(a[x]==f[2*n-e])	  {	  	f1[e+1]='L';return dfs(x+1,y);	  }	else	  if(a[y]==f[2*n-e])	  {	  	f1[e+1]='R';return dfs(x,y-1);	  }	 else	   return 0;}int main(){	freopen("palin.in","r",stdin);	freopen("palin.out","w",stdout);	T=read();	while(T--)	{		memset(f,0,sizeof(f));		n=read();		for(int i=1;i<=2*n;i++)		   a[i]=read();		if(!dfs(1,2*n))		  printf("-1\n");		else		  {		  	for(int i=1;i<2*n;i++)		     printf("%c",f1[i]);		    printf("L\n");		  }		  	 }     return 0;}/*254 1 2 4 5 3 1 2 3 533 2 1 2 1 3*/